			ID_s = i
			skills_huoshou()
			break
		end
	end
	
	id = ID_s + 1
	if id > 5 then id = 1 end
	
	for i = 1, 4 do
		if char_juese[id].siwang == false and char_juese[id].skill["祸首"] ~= "available" and id ~= _ID_s then
			_nanman_AI(source_card, ID_s, id)
		end
	    id = id + 1
