--  在富文本框中显示消息  --
function draw_messages_r()
    local t, i, j, lower
	
	t = {}; j = 1
	if #message_list <= 7 then
	    lower = 1
	else
	    lower = #message_list - 7
	end
	for i = lower, #message_list do
	    t[j] = message_list[i]
		t[j + 1] = '\n'
		j = j + 2
	end
	table.remove(t)
	txt_messages:setExpression(table.concat(t), -1)
	
	t = {}
	collectgarbage()
end

